definition.script:
pipeline {
            agent {
            kubernetes {
            containerTemplate {
            name 'curl-test'
            image 'pluhin31/wget_curl:latest'
            ttyEnabled true
            command 'sleep 180'
            }
            }
            }
            stages {
            stage('Test') {
            steps {
            container('curl-test') {
            sh """#!/bin/sh
            curl --version
            curl https://onliner.by
            wget -O - https://onliner.by | egrep "https://[0-9a-z]+[.]onliner[.]by" -o | sort -u > tmp.out
            cat tmp.out
            curl \$(cat tmp.out) -I | egrep HTTP
            """
            }
            }
            }
            }
            }
